import React, { useState, useEffect } from "react";
import StudentService from "../Services/StudentServices"; 
import { Bar } from "react-chartjs-2";  // Importing Bar chart from Chart.js
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend } from 'chart.js';
import Filters from "./Filters";
import StudentTable from "./StudentTable";
import StudentDetails from "./StudentDetails";
import "./Dashboard.css";

// Registering Chart.js components
ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

const Dashboard = ({ selectedMenu }) => {
  const [students, setStudents] = useState([]);
  const [filteredStudents, setFilteredStudents] = useState([]); // Filtered student list
  const [selectedStudent, setSelectedStudent] = useState(null); // Track the selected student

  // Fetching student data from the API
  useEffect(() => {
    StudentService.getStudents()
      .then((response) => {
        setStudents(response.data);
        setFilteredStudents(response.data); // Initial student data (unfiltered)
      })
      .catch((error) => console.error("Error fetching students:", error));
  }, []);

  // Calculate the number of non-placed students
  const calculateNonPlacedStudents = () => {
    const nonPlacedStudents = students.filter((student) => student.placementStatus !== "Placed");
    return nonPlacedStudents.length;
  };

  // Data for the non-placed students chart
  const chartData = {
    labels: ['Non-Placed Students'],
    datasets: [
      {
        label: 'Total Non-Placed Students',
        data: [calculateNonPlacedStudents()],
        backgroundColor: 'rgba(255, 99, 132, 0.2)',
        borderColor: 'rgba(255, 99, 132, 1)',
        borderWidth: 1,
      },
    ],
  };

  // Handling button click to select options
  const handleOptionClick = (option) => {
    setStudentOption(option); // Set the currently selected option
    setSelectedStudent(null); // Reset selected student when changing options
  };

  const renderStudentContent = () => {
    // Check if "Analysis" is selected from the sidebar
    if (selectedMenu === "Analysis") {
      return (
        <div className="analysis">
          <h2>Non-Placed Students Analysis</h2>
          <Bar
            data={chartData}
            options={{
              responsive: true,
              plugins: {
                legend: { position: 'top' },
                title: { display: true, text: 'Non-Placed Students' }
              }
            }}
          />
        </div>
      );
    }

    if (selectedMenu === "EnterDetails") {
      return <div>Enter Student Details Form</div>;
    }

    if (selectedMenu === "ViewDetails") {
      if (selectedStudent) {
        return (
          <div className="viewdetails">
            <button
              className="back-btn"
              onClick={() => setSelectedStudent(null)} // Go back to table
            >
              <img src="/Images/Back.png" height="25px" width="25px" />
            </button>
            <StudentDetails student={selectedStudent} />
          </div>
        );
      } else {
        return (
          <div className="viewdetails">
            <Filters onFilter={handleFilter} />
            <StudentTable
              students={filteredStudents}
              onView={handleViewStudent} // Trigger view for selected student
            />
          </div>
        );
      }
    }

    if (selectedMenu === "DownloadReports") {
      return <div>Download Reports Section</div>;
    }

    return <div className="placeholder">Select an option from the menu</div>;
  };

  return (
    <div className="dashboard">
      {renderStudentContent()}
    </div>
  );
};

export default Dashboard;
