// export default Dashboard;
import React, { useState, useEffect } from "react";
i

const Dashboard = () => {
  const [students, setStudents] = useState([]); // All students
  const [filteredStudents, setFilteredStudents] = useState([]); // Filtered students
  const [selectedStudent, setSelectedStudent] = useState(null); // Selected student for viewing

  useEffect(() => {
    // Fetch students from the API
    StudentService.getStudents()
      .then((response) => {
        console.log("Fetched students:", response.data); // Debugging
        setStudents(response.data);
        setFilteredStudents(response.data);
      })
      .catch((error) => console.error("Error fetching students:", error));
  }, []);

  const handleFilter = (key, value) => {
    let filtered = students;
    if (key === "batch") filtered = students.filter((s) => s.batch === value);
    if (key === "program") filtered = students.filter((s) => s.program === value);
    if (key === "search")
      filtered = students.filter((s) =>
        s.name.toLowerCase().includes(value.toLowerCase())
      );
    setFilteredStudents(filtered);
  };

  const handleViewStudent = (student) => {
    setSelectedStudent(student);
  };

  const handleEditStudent = (student) => {
    alert(`Edit student with ID: ${student.id}`);
  };

  return (
    <div className="dashboard">
      <div className="main-content">
        <div className="content">
          <StudentTable
            students={filteredStudents}
            onView={handleViewStudent}
            onEdit={handleEditStudent}
          />
          <StudentDetails student={selectedStudent} />
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
