import React from "react";
// import "./CompanyDetails.css"; // Ensure to style the table properly
import { jsPDF } from "jspdf";
import "jspdf-autotable";

const CompanyDetails = ({ company }) => {
    
  const downloadPDF = () => {
    const doc = new jsPDF();

    // Title
    doc.setFontSize(18);
    doc.text(`${company.name}`, 10, 10);

    // General Company Details
    const generalDetails = [
      ["Name", company.name],
      ["Batch", company.batch],
      ["Address", company.address
        ? `${company.address.blockNo}, ${company.address.buildingName}, ${company.address.area}, ${company.address.city}, ${company.address.state} - ${company.address.pincode}`
        : "N/A"],
      ["Contact Person", `${company.contactPerson.name} (${company.contactPerson.designation})`],
      ["Email", company.contactPerson.email],
      ["Mobile", company.contactPerson.mobile],
    ];

    doc.autoTable({
      head: [["Field", "Value"]],
      body: generalDetails,
      startY: 20,
    });

    // Designations and Placement Process
    company.designations.forEach((designation, index) => {
      doc.addPage();
      doc.setFontSize(16);
      doc.text(`Designation ${index + 1}: ${designation.designation}`, 10, 10);

      const designationDetails = [
        ["Bond", designation.bond || "N/A"],
        ["Location", designation.location || "N/A"],
        [
          "Required Qualifications",
          designation.requiredQualifications ? designation.requiredQualifications.join(", ") : "N/A",
        ],
        ["Package", designation.package || "N/A"],
      ];

      doc.autoTable({
        head: [["Field", "Value"]],
        body: designationDetails,
        startY: 20,
      });

      doc.setFontSize(14);
      doc.text(`Placement Process:`, 10, doc.lastAutoTable.finalY + 10);

      const processDetails = designation.placementProcess.map((process) => [
        `Round ${process.roundNumber}: ${process.round}`,
        process.description,
      ]);

      doc.autoTable({
        head: [["Round", "Description"]],
        body: processDetails,
        startY: doc.lastAutoTable.finalY + 15,
      });
    });

    doc.save(`${company.name}.pdf`);
  };

  return (
    <div className="company-details-container">
      <h3>Company Details</h3>
      <div className="details-layout">
        {/* General Company Details */}
        <table className="details-table">
          <tbody>
            <tr>
              <th>Name</th>
              <td>{company.name}</td>
            </tr>
            <tr>
              <th>Batch</th>
              <td>{company.batch}</td>
            </tr>
            <tr>
              <th>Address</th>
              <td>
                {company.address
                  ? `${company.address.blockNo}, ${company.address.buildingName}, ${company.address.area}, ${company.address.city}, ${company.address.state} - ${company.address.pincode}`
                  : "N/A"}
              </td>
            </tr>
            <tr>
              <th>Contact Person</th>
              <td>{`${company.contactPerson.name} (${company.contactPerson.designation})`}</td>
            </tr>
            <tr>
              <th>Email</th>
              <td>{company.contactPerson.email}</td>
            </tr>
            <tr>
              <th>Mobile</th>
              <td>{company.contactPerson.mobile}</td>
            </tr>
          </tbody>
        </table>

        {/* Designation Details */}
        {company.designations.map((designation, index) => (
          <div key={index} className="designation-section">
            <h4>Designation {index + 1}: {designation.designation}</h4>
            <table className="details-table">
              <tbody>
                <tr>
                  <th>Bond</th>
                  <td>{designation.bond || "N/A"}</td>
                </tr>
                <tr>
                  <th>Location</th>
                  <td>{designation.location || "N/A"}</td>
                </tr>
                <tr>
                  <th>Required Qualifications</th>
                  <td>
                    {designation.requiredQualifications
                      ? designation.requiredQualifications.join(", ")
                      : "N/A"}
                  </td>
                </tr>
                <tr>
                  <th>Package</th>
                  <td>{designation.package || "N/A"}</td>
                </tr>
              </tbody>
            </table>

            <h5>Placement Process:</h5>
            <table className="details-table">
              <thead>
                <tr>
                  <th>Round</th>
                  <th>Description</th>
                </tr>
              </thead>
              <tbody>
                {designation.placementProcess.map((process, idx) => (
                  <tr key={idx}>
                    <td>{`Round ${process.roundNumber}: ${process.round}`}</td>
                    <td>{process.description}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        ))}
      </div>
      <button className="download" onClick={downloadPDF}>
        Download PDF
      </button>
    </div>
  );
};

export default CompanyDetails;
