import React, { useState, useEffect } from "react";
import StudentService from "../Services/StudentServices"; 
import StudentTable from "./StudentTable";
import Filters from "./Filters";
import StudentDetails from "./StudentDetails"; // Assuming you have a StudentDetails component
import "./Dashboard.css";

const Dashboard = () => {
  const [studentOption, setStudentOption] = useState("");
  const [students, setStudents] = useState([]);
  const [filteredStudents, setFilteredStudents] = useState([]);
  const [selectedStudent, setSelectedStudent] = useState(null); // Track the selected student

  // Fetching student data from the API on component mount
  useEffect(() => {
    StudentService.getStudents()
      .then((response) => {
        setStudents(response.data);
        setFilteredStudents(response.data);
      })
      .catch((error) => console.error("Error fetching students:", error));
  }, []);

  // Handling button click to select options
  const handleOptionClick = (option) => {
    setStudentOption(option);
  };

  // Handling filter logic
  const handleFilter = (key, value) => {
    let filtered = students;
    if (key === "batch") filtered = students.filter((s) => s.batch === value);
    if (key === "program") filtered = students.filter((s) => s.program === value);
    if (key === "search")
      filtered = students.filter((s) =>
        s.name.toLowerCase().includes(value.toLowerCase())
      );
    setFilteredStudents(filtered);
  };

  // Handling student selection for details view
  const handleViewStudent = (student) => {
    setSelectedStudent(student); // Set the selected student to display details
  };

  // Function to go back to the student table
  const handleBackToTable = () => {
    setSelectedStudent(null); // Reset the selected student to null
  };

  const renderStudentContent = () => {
    if (selectedStudent) {
      return (
        <div className="viewdetails">
          {/* Back Button */}
          <button className="back-btn" onClick={handleBackToTable}>
            <img src="/Images/Back.png" height="15px" width="15px" />
            Back to Student Table
          </button>
          <StudentDetails student={selectedStudent} />
        </div>
      );
    }

    // If no student is selected, render the student table
    return (
      <div className="viewdetails">
        <Filters onFilter={handleFilter} />
        <div className="content">
          <StudentTable
            students={filteredStudents}
            onView={handleViewStudent} // Set the selected student when view is clicked
          />
        </div>
      </div>
    );
  };

  return (
    <div className="dashboard">
      {/* Navigation Buttons */}
      <div className="buttons">
        <button onClick={() => handleOptionClick("EnterDetails")}>
          <img src="/Images/Side errow.png" height="15px" width="15px" /> Enter Details
        </button>
        <button onClick={() => handleOptionClick("DownloadReports")}>
          <img src="/Images/Side errow.png" height="15px" width="15px" /> Download Reports
          <img src="/Images/download.png" height="18px" width="27px" />
        </button>
        <br />
        <button onClick={() => handleOptionClick("ViewDetails")}>
          <img src="/Images/Side errow.png" height="15px" width="15px" /> View Details
        </button>
      </div>

      {/* Main Section */}
      <div className="main-section">
        {renderStudentContent()}
      </div>
    </div>
  );
};

export default Dashboard;
