import React, { useEffect, useState } from "react";
import { Pie } from "react-chartjs-2"; // Import Pie chart from Chart.js
import {
  Chart as ChartJS,
  ArcElement,
  Tooltip,
  Legend,
} from "chart.js";
import StudentService from "../Services/StudentServices"; // Import your service to fetch student data

// Register Chart.js components
ChartJS.register(ArcElement, Tooltip, Legend);

const AnalysisDashboard = () => {
  const [placedCount, setPlacedCount] = useState(0);
  const [nonPlacedCount, setNonPlacedCount] = useState(0);
  const [pending, setpendingCount] = useState(0);

  // Fetch student data and calculate placed/non-placed counts
  useEffect(() => {
    StudentService.getStudents()
      .then((response) => {
        const students = response.data;
        const placed = students.filter((student) => student.placementStatus === "placed").length;
        const nonPlaced = students.filter((student) => student.placementStatus === "not placed").length;
        const pending = students.filter((student) => student.placementStatus === "pending").length;
        
        setPlacedCount(placed);
        setNonPlacedCount(nonPlaced);
        setNonPlacedCount(nonPlaced);

      })
      .catch((error) => console.error("Error fetching student data:", error));
  }, []);

  // Data for the Pie Chart
  const pieChartData = {
    labels: ["Placed Students", "Non-Placed Students"],
    datasets: [
      {
        data: [placedCount, nonPlacedCount],
        backgroundColor: ["#4CAF50", "#FF5252"], // Colors for each segment
        hoverBackgroundColor: ["#66BB6A", "#FF867F"], // Hover colors
        borderColor: ["#ffffff", "#ffffff"], // Border color
        borderWidth: 2,
      },
    ],
  };

  // Options for the Pie Chart
  const pieChartOptions = {
    responsive: true,
    plugins: {
      legend: { position: "top" },
      tooltip: { enabled: true },
    },
  };

  return (
    <div className="analysis-dashboard">
      <h2>Placement Analysis</h2>
      <Pie data={pieChartData} options={pieChartOptions} />
    </div>
  );
};

export default AnalysisDashboard;
