import React, { useEffect, useState } from "react";
import "./EnterResultDashboard.css";

const EnterResultDashboard = () => {

const [option, setOption] = useState("EnterDetails"); // Default to "EnterDetails" 
const handleOptionClick = (option) => {
    setOption(option); // Set the currently selected option
  }; 
    {/* Navigation Buttons for Student Options */}
      {/* Breadcrumb */}
      const renderStudentContent = () => {
        if (Option === "EnterDetails") {
          return <div></div>;
        }
    
        if (studentOption === "ViewDetails") {
          if (selectedStudent) {
            return (
              <div className="viewdetails">
                <button
                  className="back-btn"
                  onClick={() => setSelectedStudent(null)} // Go back to table
                >
                  <img src="/Images/Back.png" height="25px" width="25px" />
                </button>
                <StudentDetails student={selectedStudent} />
              </div>
            );
          } else {
            return (
              <div className="viewdetails">
                <Filters onFilter={handleFilter} downloadCSV={downloadFilteredCSV} downloadPDF={ downloadFilteredPDF}/>
                <StudentTable
                  students={filteredStudents}
                  onView={handleViewStudent} // Trigger view for selected student
                  onDelete={onDelete}
                />
              </div>
            );
          }
        }
    
        if (studentOption === "DownloadReports") {
          return <div></div>;
        }
    
        return null;
    };
    
    return (
        
    <div className="enter-buttons">
    <div className="breadcrumb">
    Dashboard &gt; Result
  </div>
      <button onClick={() => handleOptionClick("EnterDetails")}>
        <img src="/Images/Side errow.png" height="15px" width="15px" /> Enter Result
      </button>
      <br />
      <button onClick={() => handleOptionClick("ViewDetails")}>
        <img src="/Images/Side errow.png" height="15px" width="15px" /> View Result
      </button>
    </div>

      );
};
export default EnterResultDashboard;
