import React, { useState, useEffect } from "react";
import StudentService from "../Services/StudentServices";
import StudentTable from "./StudentTable";
import Filters from "./Filters";
import { jsPDF } from "jspdf";
import "jspdf-autotable";
import StudentDetails from "./StudentDetails";
import "./Dashboard.css";

const Dashboard = ({ selectedMenu }) => {
  const [studentOption, setStudentOption] = useState();
  const [students, setStudents] = useState([]);
  const [filteredStudents, setFilteredStudents] = useState([]);
  const [selectedStudent, setSelectedStudent] = useState(null);

  useEffect(() => {
    StudentService.getStudents()
      .then((response) => {
        setStudents(response.data);
      })
      .catch((error) => console.error("Error fetching students:", error));
  }, []);

  const handleOptionClick = (option) => {
    setStudentOption(option);
    setSelectedStudent(null);
  };

  const handleFilter = (key, value) => {
    let filtered = [...students];
    const batch = document.querySelector("select[name='batch']").value;

    if (key === "batch" && value) {
      filtered = filtered.filter((s) => s.batch === value);
      setFilteredStudents(filtered);
      return;
    }

    if (!batch) {
      if (key === "search" && value) {
        filtered = filtered.filter((s) =>
          s.name.toLowerCase().includes(value.toLowerCase())
        );
        setFilteredStudents(filtered);
      } else {
        setFilteredStudents([]); // Clear other filters
      }
      return;
    }

    if (batch) {
      if (key === "program" && value) {
        filtered = filtered.filter((s) => s.course === value);
      }
      if (key === "search" && value) {
        filtered = filtered.filter((s) =>
          s.name.toLowerCase().includes(value.toLowerCase())
        );
      }
      if (key === "sscPercentage" && value) {
        const sscLimit = parseInt(value, 10);
        filtered = filtered.filter((s) => s.ssc.percentage <= sscLimit);
      }
      if (key === "hscPercentage" && value) {
        const hscLimit = parseInt(value, 10);
        filtered = filtered.filter((s) => s.hsc.percentage <= hscLimit);
      }
      if (key === "bachelorPercentage" && value) {
        const bachelorLimit = parseInt(value, 10);
        filtered = filtered.filter((s) => s.bachelor.percentage <= bachelorLimit);
      }
      if (key === "drops" && value) {
        filtered = filtered.filter((s) => s.drops === parseInt(value, 10));
      }
      setFilteredStudents(filtered);
    } else {
      setFilteredStudents([]); // If no batch, clear all filters except search
    }
  };

  const handleViewStudent = (student) => {
    setSelectedStudent(student);
  };

  const renderStudentContent = () => {
    if (studentOption === "ViewDetails") {
      if (selectedStudent) {
        return (
          <div className="viewdetails">
            <button
              className="back-btn"
              onClick={() => setSelectedStudent(null)}
            >
              <img src="/Images/Back.png" height="25px" width="25px" alt="Back" />
            </button>
            <StudentDetails student={selectedStudent} />
          </div>
        );
      } else {
        return (
          <div className="viewdetails">
            <Filters onFilter={handleFilter} />
            <StudentTable
              students={filteredStudents}
              onView={handleViewStudent}
            />
          </div>
        );
      }
    }
    return null;
  };

  const rendersContent = () => {
    if (selectedMenu === "Student") {
      return (
        <>
          <div className="buttons">
            <button onClick={() => handleOptionClick("ViewDetails")}>
              View Details
            </button>
          </div>
          <div className="main-section">{renderStudentContent()}</div>
        </>
      );
    }
    return null;
  };

  return <div className="dashboard">{rendersContent()}</div>;
};

export default Dashboard;
