import React, { useState } from "react";
import axios from "axios";
// import "./CompanyDetailsForm.css";

const CompanyDetailsForm = () => {
  const [formData, setFormData] = useState({
    name: "",
    batch: "",
    address: {
      blockNo: "",
      buildingName: "",
      area: "",
      landmark: "",
      state: "",
      city: "",
      pincode: "",
    },
    contactPerson: {
      name: "",
      designation: "",
      email: "",
      mobile: "",
    },
    designations: [],
  });

  const [currentDesignation, setCurrentDesignation] = useState({
    designation: "",
    package: "",
    bond: "",
    location: "",
    requiredQualifications: [],
    placementProcess: [],
  });

  const [currentProcess, setCurrentProcess] = useState({
    roundNumber: "",
    round: "",
    description: "",
  });

  // General input handler for nested fields
  const handleNestedChange = (e, parentKey) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [parentKey]: {
        ...formData[parentKey],
        [name]: value,
      },
    });
  };

  // Add designations to the list
  const handleAddDesignation = () => {
    setFormData({
      ...formData,
      designations: [...formData.designations, currentDesignation],
    });
    setCurrentDesignation({
      designation: "",
      package: "",
      bond: "",
      location: "",
      requiredQualifications: [],
      placementProcess: [],
    });
  };

  // Add placement process round to the current designation
  const handleAddProcess = () => {
    setCurrentDesignation({
      ...currentDesignation,
      placementProcess: [...currentDesignation.placementProcess, currentProcess],
    });
    setCurrentProcess({ roundNumber: "", round: "", description: "" });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:8080/api/companies", formData);
      alert("Company details submitted successfully!");
      console.log(response.data);
    } catch (error) {
      console.error("Error submitting form:", error);
      alert("Failed to submit form. Please try again.");
    }
  };

  return (
    <div style={{ maxWidth: "800px", margin: "0 auto", padding: "20px" }}>
      <h2>Enter Company Details</h2>
      <form onSubmit={handleSubmit}>
        {/* Basic Details */}
        <div>
          <label>Company Name:</label>
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={(e) => setFormData({ ...formData, name: e.target.value })}
            required
          />
        </div>
        <div>
          <label>Batch:</label>
          <input
            type="text"
            name="batch"
            value={formData.batch}
            onChange={(e) => setFormData({ ...formData, batch: e.target.value })}
            required
          />
        </div>

        {/* Address */}
        <h3>Address</h3>
        {Object.keys(formData.address).map((key) => (
          <div key={key}>
            <label>{key.charAt(0).toUpperCase() + key.slice(1)}:</label>
            <input
              type="text"
              name={key}
              value={formData.address[key]}
              onChange={(e) => handleNestedChange(e, "address")}
            />
          </div>
        ))}

        {/* Contact Person */}
        <h3>Contact Person</h3>
        {Object.keys(formData.contactPerson).map((key) => (
          <div key={key}>
            <label>{key.charAt(0).toUpperCase() + key.slice(1)}:</label>
            <input
              type="text"
              name={key}
              value={formData.contactPerson[key]}
              onChange={(e) => handleNestedChange(e, "contactPerson")}
              required
            />
          </div>
        ))}

        {/* Designations */}
        <h3>Designations</h3>
        <div>
          <label>Designation:</label>
          <input
            type="text"
            name="designation"
            value={currentDesignation.designation}
            onChange={(e) => setCurrentDesignation({ ...currentDesignation, designation: e.target.value })}
            required
          />
        </div>
        <div>
          <label>Package:</label>
          <input
            type="text"
            name="package"
            value={currentDesignation.package}
            onChange={(e) => setCurrentDesignation({ ...currentDesignation, package: e.target.value })}
            required
          />
        </div>
        <div>
          <label>Bond:</label>
          <input
            type="text"
            name="bond"
            value={currentDesignation.bond}
            onChange={(e) => setCurrentDesignation({ ...currentDesignation, bond: e.target.value })}
            required
          />
        </div>
        <div>
          <label>Location:</label>
          <input
            type="text"
            name="location"
            value={currentDesignation.location}
            onChange={(e) => setCurrentDesignation({ ...currentDesignation, location: e.target.value })}
            required
          />
        </div>

        {/* Required Qualifications */}
        <div>
          <label>Required Qualifications (comma-separated):</label>
          <input
            type="text"
            name="requiredQualifications"
            value={currentDesignation.requiredQualifications.join(", ")}
            onChange={(e) =>
              setCurrentDesignation({ ...currentDesignation, requiredQualifications: e.target.value.split(",") })
            }
          />
        </div>

        {/* Placement Process */}
        <h4>Placement Process</h4>
        <div>
          <label>Round Number:</label>
          <input
            type="number"
            name="roundNumber"
            value={currentProcess.roundNumber}
            onChange={(e) => setCurrentProcess({ ...currentProcess, roundNumber: e.target.value })}
            required
          />
        </div>
        <div>
          <label>Round:</label>
          <input
            type="text"
            name="round"
            value={currentProcess.round}
            onChange={(e) => setCurrentProcess({ ...currentProcess, round: e.target.value })}
            required
          />
        </div>
        <div>
          <label>Description:</label>
          <input
            type="text"
            name="description"
            value={currentProcess.description}
            onChange={(e) => setCurrentProcess({ ...currentProcess, description: e.target.value })}
            required
          />
        </div>
        <button type="button" onClick={handleAddProcess}>
          Add Process
        </button>

        {/* Add Designation */}
        <button type="button" onClick={handleAddDesignation}>
          Add Designation
        </button>

        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default CompanyDetailsForm;
