import React, { useState } from 'react';
import './CompanyDetilsForm.css';

const CompanyDetilsForm = () => {
  const [formData, setFormData] = useState({
    name: '',
    batch: '',
    address: {
      blockNo: '',
      buildingName: '',
      area: '',
      landmark: '',
      state: '',
      city: '',
      pincode: '',
    },
    contactPerson: {
      name: '',
      designation: '',
      email: '',
      mobile: '',
    },
    designations: [],
  });

  // Handle change for simple fields
  const handleChange = (field, value) => {
    setFormData({ ...formData, [field]: value });
  };

  // Handle nested field changes
  const handleNestedChange = (section, field, value) => {
    setFormData({
      ...formData,
      [section]: {
        ...formData[section],
        [field]: value,
      },
    });
  };

  // Handle designations field change
  const handleDesignationChange = (index, field, value) => {
    const updatedDesignations = [...formData.designations];
    updatedDesignations[index][field] = value;
    setFormData({ ...formData, designations: updatedDesignations });
  };

  // Add new designation
  const addDesignation = () => {
    setFormData({
      ...formData,
      designations: [
        ...formData.designations,
        {
          designation: '',
          package: '',
          bond: '',
          location: '',
          requiredQualifications: [],
          placementProcess: [],
        },
      ],
    });
  };

  // Remove a designation
  const removeDesignation = (index) => {
    const updatedDesignations = formData.designations.filter(
      (_, idx) => idx !== index
    );
    setFormData({ ...formData, designations: updatedDesignations });
  };

  // Submit form
  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form submitted:', formData);
    // Here, you can send the formData to your backend API
  };

  return (
    <form className="company-form" onSubmit={handleSubmit}>
      <h2>Company Details</h2>

      {/* Company Name */}
      <label>Company Name</label>
      <input
        type="text"
        value={formData.name}
        onChange={(e) => handleChange('name', e.target.value)}
        required
      />

      {/* Batch */}
      <label>Batch</label>
      <input
        type="text"
        value={formData.batch}
        onChange={(e) => handleChange('batch', e.target.value)}
        required
      />

      {/* Address */}
      <h3>Address</h3>
      <label>Block No</label>
      <input
        type="text"
        value={formData.address.blockNo}
        onChange={(e) => handleNestedChange('address', 'blockNo', e.target.value)}
      />
      <label>Building Name</label>
      <input
        type="text"
        value={formData.address.buildingName}
        onChange={(e) =>
          handleNestedChange('address', 'buildingName', e.target.value)
        }
      />
      <label>Area</label>
      <input
        type="text"
        value={formData.address.area}
        onChange={(e) => handleNestedChange('address', 'area', e.target.value)}
      />
      <label>Landmark</label>
      <input
        type="text"
        value={formData.address.landmark}
        onChange={(e) =>
          handleNestedChange('address', 'landmark', e.target.value)
        }
      />
      <label>State</label>
      <input
        type="text"
        value={formData.address.state}
        onChange={(e) => handleNestedChange('address', 'state', e.target.value)}
      />
      <label>City</label>
      <input
        type="text"
        value={formData.address.city}
        onChange={(e) => handleNestedChange('address', 'city', e.target.value)}
      />
      <label>Pincode</label>
      <input
        type="text"
        value={formData.address.pincode}
        onChange={(e) => handleNestedChange('address', 'pincode', e.target.value)}
      />

      {/* Contact Person */}
      <h3>Contact Person</h3>
      <label>Name</label>
      <input
        type="text"
        value={formData.contactPerson.name}
        onChange={(e) =>
          handleNestedChange('contactPerson', 'name', e.target.value)
        }
        required
      />
      <label>Designation</label>
      <input
        type="text"
        value={formData.contactPerson.designation}
        onChange={(e) =>
          handleNestedChange('contactPerson', 'designation', e.target.value)
        }
        required
      />
      <label>Email</label>
      <input
        type="email"
        value={formData.contactPerson.email}
        onChange={(e) =>
          handleNestedChange('contactPerson', 'email', e.target.value)
        }
        required
      />
      <label>Mobile</label>
      <input
        type="text"
        value={formData.contactPerson.mobile}
        onChange={(e) =>
          handleNestedChange('contactPerson', 'mobile', e.target.value)
        }
        required
      />

      {/* Designations */}
      <h3>Designations</h3>
      {formData.designations.map((designation, index) => (
        <div key={index} className="designation">
          <label>Designation</label>
          <input
            type="text"
            value={designation.designation}
            onChange={(e) =>
              handleDesignationChange(index, 'designation', e.target.value)
            }
            required
          />
          <label>Package</label>
          <input
            type="text"
            value={designation.package}
            onChange={(e) =>
              handleDesignationChange(index, 'package', e.target.value)
            }
            required
          />
          <label>Bond</label>
          <input
            type="text"
            value={designation.bond}
            onChange={(e) =>
              handleDesignationChange(index, 'bond', e.target.value)
            }
            required
          />
          <label>Location</label>
          <input
            type="text"
            value={designation.location}
            onChange={(e) =>
              handleDesignationChange(index, 'location', e.target.value)
            }
            required
          />
          <button type="button" onClick={() => removeDesignation(index)}>
            Remove Designation
          </button>
        </div>
      ))}
      <button type="button" onClick={addDesignation}>
        Add Designation
      </button>

      {/* Submit */}
      <button type="submit">Submit</button>
    </form>
  );
};

export default CompanyDetilsForm;
