import React, { useEffect, useState } from "react";
import CompanyService from "../Services/CompanyServices"; // Use the instance directly
import ResultFilters from "./ResultFilters";
import "./CompanyDashboard.css";
import CompanyDeatilsForm from "./CompanyDetailsForm";
import CompanyTable from "./CompanyTable";

const CompanyDashboard = ({ selectedMenu }) => {
    const [option, setOption] = useState("entercompany"); // Default to "entercompany"
    const [filteredCompany, setFilteredCompany] = useState([]);
    const [selectedCompany, setSelectedCompany] = useState(null);
    const [companies, setCompanies] = useState([]);

    // Fetch companies on component mount
    useEffect(() => {
        CompanyService.getCompany()
            .then((response) => {
                setCompanies(response.data);
                setFilteredCompany(response.data);
            })
            .catch((error) => console.error("Error fetching companies:", error));
    }, []);

    const handleFilter = (key, value) => {
        let filtered = [...companies];

        if (key === "clear") {
            setFilteredCompany([]);
            return;
        }

        if (key === "batch" && value) {
            filtered = filtered.filter((company) => company.batch === value);
        }

        if (key === "program" && value) {
            filtered = filtered.filter((company) => company.course === value);
        }

        if (key === "search" && value) {
            filtered = filtered.filter((company) =>
                company.name.toLowerCase().includes(value.toLowerCase())
            );
        }

        setFilteredCompany(filtered);
    };

    const handleOptionClick = (selectedOption) => {
        setOption(selectedOption);
    };

    const handleViewCompany = (company) => {
        setSelectedCompany(company);
    };

    const onDelete = (id) => {
        CompanyService.deleteCompany(id)
            .then(() => {
                const updatedCompanies = companies.filter((company) => company.id !== id);
                setCompanies(updatedCompanies);
                setFilteredCompany(updatedCompanies);
            })
            .catch((error) => console.error("Error deleting company:", error));
    };

    const renderResultContent = () => {
        if (option === "entercompany") {
            return (
                <div className="entercompany">
                    <h3 className="cheadings">Company Details</h3>
                    <CompanyDeatilsForm />
                </div>
            );
        }

        if (option === "viewcompany") {
            return (
                <div className="viewcompany">
                    <ResultFilters onFilter={handleFilter} />
                    <CompanyTable
                        company={filteredCompany}
                        onView={handleViewCompany}
                        onDelete={onDelete}
                    />
                </div>
            );
        }

        return null;
    };

    const renderContent = () => {
        if (selectedMenu === "Company") {
            return (
                <>
                    <div className="cmp-buttons">
                        <div className="breadcrumb">Dashboard &gt; Company</div>
                        <button onClick={() => handleOptionClick("entercompany")}>
                            <img
                                src="/Images/Side errow.png"
                                height="15px"
                                width="15px"
                                alt="Arrow"
                            />{" "}
                            Enter Company Details
                        </button>
                        <br />
                        <button onClick={() => handleOptionClick("viewcompany")}>
                            <img
                                src="/Images/Side errow.png"
                                height="15px"
                                width="15px"
                                alt="Arrow"
                            />{" "}
                            View Company Details
                        </button>
                    </div>

                    <div className="main-section">{renderResultContent()}</div>
                </>
            );
        }

        return <div></div>;
    };

    return <div className="CompanyDashboard">{renderContent()}</div>;
};

export default CompanyDashboard;
