const Dashboard = ({ selectedMenu }) => {
  const [studentOption, setStudentOption] = useState("ViewDetails"); // Default to "ViewDetails"
  const [students, setStudents] = useState([]); // Full student list
  const [filteredStudents, setFilteredStudents] = useState([]); // Filtered students
  const [selectedStudent, setSelectedStudent] = useState(null); // Track the selected student
  const [activeFilters, setActiveFilters] = useState({}); // Store current filter criteria

  // Fetch student data on component mount
  useEffect(() => {
    StudentService.getStudents()
      .then((response) => {
        setStudents(response.data);
        setFilteredStudents(response.data); // Initially show all students
      })
      .catch((error) => console.error("Error fetching students:", error));
  }, []);

  // Handle filter logic
  const handleFilter = (key, value) => {
    const newFilters = { ...activeFilters, [key]: value };

    // Remove empty filters
    if (value === "") {
      delete newFilters[key];
    }

    setActiveFilters(newFilters); // Update active filters

    // Apply filters
    let filtered = students;

    Object.entries(newFilters).forEach(([filterKey, filterValue]) => {
      if (filterKey === "batch") {
        filtered = filtered.filter((s) => s.batch === filterValue);
      } else if (filterKey === "program") {
        filtered = filtered.filter((s) => s.course === filterValue);
      } else if (filterKey === "search") {
        filtered = filtered.filter((s) =>
          s.name.toLowerCase().includes(filterValue.toLowerCase())
        );
      } else if (filterKey === "sscPercentage") {
        const sscLimit = parseInt(filterValue, 10);
        filtered = filtered.filter((s) => s.ssc.percentage <= sscLimit);
      } else if (filterKey === "hscPercentage") {
        const hscLimit = parseInt(filterValue, 10);
        filtered = filtered.filter((s) => s.hsc.percentage <= hscLimit);
      } else if (filterKey === "bachelorPercentage") {
        const bachelorLimit = parseInt(filterValue, 10);
        filtered = filtered.filter((s) => s.bachelor.percentage <= bachelorLimit);
      } else if (filterKey === "drops") {
        filtered = filtered.filter((s) => s.drops === parseInt(filterValue, 10));
      }
    });

    setFilteredStudents(filtered); // Update the filtered students
  };

  // Handle student selection for details view
  const handleViewStudent = (student) => {
    setSelectedStudent(student); // Set the selected student
  };

  // Handle returning to the student list
  const handleBackToTable = () => {
    setSelectedStudent(null); // Reset selected student
  };

  // Render content based on `studentOption`
  const renderStudentContent = () => {
    if (studentOption === "ViewDetails") {
      if (selectedStudent) {
        // Show student details
        return (
          <div className="viewdetails">
            <button className="back-btn" onClick={handleBackToTable}>
              <img src="/Images/Back.png" alt="Back" height="25px" width="25px" />
            </button>
            <StudentDetails student={selectedStudent} />
          </div>
        );
      } else {
        // Show filters and table
        return (
          <div className="viewdetails">
            <Filters
              onFilter={handleFilter}
              downloadCSV={downloadFilteredCSV}
              downloadPDF={downloadFilteredPDF}
            />
            <StudentTable
              students={filteredStudents}
              onView={handleViewStudent}
              onDelete={onDelete}
            />
          </div>
        );
      }
    }

    if (studentOption === "EnterDetails") {
      return <div>Enter Student Details Form</div>;
    }

    if (studentOption === "DownloadReports") {
      return <div>Download Reports Section</div>;
    }

    return null;
  };

  // Main rendering logic
  return (
    <div className="dashboard">
      {selectedMenu === "Student" && (
        <>
          {/* Navigation Buttons */}
          <div className="buttons">
            <div className="breadcrumb">Dashboard &gt; Student</div>
            <button onClick={() => setStudentOption("EnterDetails")}>
              <img src="/Images/Side errow.png" height="15px" width="15px" /> Enter Details
            </button>
            <button onClick={() => setStudentOption("DownloadReports")}>
              <img src="/Images/Side errow.png" height="15px" width="15px" /> Download Reports
            </button>
            <button onClick={() => setStudentOption("ViewDetails")}>
              <img src="/Images/Side errow.png" height="15px" width="15px" /> View Details
            </button>
          </div>

          {/* Render student-specific content */}
          <div className="main-section">{renderStudentContent()}</div>
        </>
      )}
    </div>
  );
};

export default Dashboard;
