import React, { useState } from 'react';
import './App.css';

const App = () => {
  const [formData, setFormData] = useState({
    companyName: '',
    batch: '',
    address: {
      blockNo: '',
      buildingName: '',
      area: '',
      landmark: '',
      state: '',
      city: '',
      pincode: '',
    },
    contactPerson: {
      name: '',
      designation: '',
      email: '',
      mobile: '',
    },
    designations: [],
  });

  const [newDesignation, setNewDesignation] = useState({
    designation: '',
    package: '',
    bond: '',
    location: '',
    requiredQualifications: '',
    placementProcess: [],
  });

  const [newProcess, setNewProcess] = useState({
    roundNumber: '',
    round: '',
    description: '',
  });

  const handleChange = (e, section, subsection) => {
    const { name, value } = e.target;
    if (subsection) {
      setFormData((prevState) => ({
        ...prevState,
        [section]: {
          ...prevState[section],
          [subsection]: {
            ...prevState[section][subsection],
            [name]: value,
          },
        },
      }));
    } else if (section) {
      setFormData((prevState) => ({
        ...prevState,
        [section]: {
          ...prevState[section],
          [name]: value,
        },
      }));
    } else {
      setFormData((prevState) => ({
        ...prevState,
        [name]: value,
      }));
    }
  };

  const handleAddDesignation = () => {
    setFormData((prevState) => ({
      ...prevState,
      designations: [...prevState.designations, newDesignation],
    }));
    setNewDesignation({
      designation: '',
      package: '',
      bond: '',
      location: '',
      requiredQualifications: '',
      placementProcess: [],
    });
  };

  const handleAddProcess = () => {
    setNewDesignation((prevState) => ({
      ...prevState,
      placementProcess: [...prevState.placementProcess, newProcess],
    }));
    setNewProcess({ roundNumber: '', round: '', description: '' });
  };

  const handleRemoveDesignation = (index) => {
    setFormData((prevState) => ({
      ...prevState,
      designations: prevState.designations.filter((_, i) => i !== index),
    }));
  };

  const handleRemoveProcess = (index) => {
    setNewDesignation((prevState) => ({
      ...prevState,
      placementProcess: prevState.placementProcess.filter((_, i) => i !== index),
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:5000/api/companies', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData),
      });

      if (response.ok) {
        const data = await response.json();
        console.log('Response:', data);
        alert('Company details submitted successfully!');
      } else {
        console.error('Error:', response.statusText);
        alert('Failed to submit the form.');
      }
    } catch (error) {
      console.error('Error submitting data:', error);
      alert('An error occurred while submitting the form.');
    }
  };

  return (
    <div className="App">
      <h1>Company Details Form</h1>
      <form onSubmit={handleSubmit}>
        {/* Company Info */}
        <div>
          <label>Company Name: </label>
          <input
            type="text"
            name="companyName"
            value={formData.companyName}
            onChange={(e) => handleChange(e)}
            required
          />
        </div>
        <div>
          <label>Batch: </label>
          <input
            type="text"
            name="batch"
            value={formData.batch}
            onChange={(e) => handleChange(e)}
            required
          />
        </div>

        {/* Address */}
        <fieldset>
          <legend>Address</legend>
          <input
            type="text"
            placeholder="Block No"
            name="blockNo"
            value={formData.address.blockNo}
            onChange={(e) => handleChange(e, 'address')}
          />
          <input
            type="text"
            placeholder="Building Name"
            name="buildingName"
            value={formData.address.buildingName}
            onChange={(e) => handleChange(e, 'address')}
          />
          {/* Add other address fields similarly */}
        </fieldset>

        {/* Contact Person */}
        <fieldset>
          <legend>Contact Person</legend>
          <input
            type="text"
            placeholder="Name"
            name="name"
            value={formData.contactPerson.name}
            onChange={(e) => handleChange(e, 'contactPerson')}
            required
          />
          <input
            type="text"
            placeholder="Designation"
            name="designation"
            value={formData.contactPerson.designation}
            onChange={(e) => handleChange(e, 'contactPerson')}
            required
          />
          {/* Add email and mobile fields similarly */}
        </fieldset>

        {/* Designations */}
        <fieldset>
          <legend>Designations</legend>
          <input
            type="text"
            placeholder="Designation"
            name="designation"
            value={newDesignation.designation}
            onChange={(e) => setNewDesignation({ ...newDesignation, designation: e.target.value })}
          />
          {/* Add other fields for package, bond, location, etc. */}

          {/* Placement Process */}
          <fieldset>
            <legend>Placement Process</legend>
            <input
              type="number"
              placeholder="Round Number"
              name="roundNumber"
              value={newProcess.roundNumber}
              onChange={(e) => setNewProcess({ ...newProcess, roundNumber: e.target.value })}
            />
            <input
              type="text"
              placeholder="Round"
              name="round"
              value={newProcess.round}
              onChange={(e) => setNewProcess({ ...newProcess, round: e.target.value })}
            />
            <textarea
              placeholder="Description"
              name="description"
              value={newProcess.description}
              onChange={(e) => setNewProcess({ ...newProcess, description: e.target.value })}
            />
            <button type="button" onClick={handleAddProcess}>Add Process</button>
            {newDesignation.placementProcess.map((process, index) => (
              <div key={index}>
                <span>{process.round}: {process.description}</span>
                <button type="button" onClick={() => handleRemoveProcess(index)}>Remove</button>
              </div>
            ))}
          </fieldset>

          <button type="button" onClick={handleAddDesignation}>Add Designation</button>
          {formData.designations.map((designation, index) => (
            <div key={index}>
              <span>{designation.designation}</span>
              <button type="button" onClick={() => handleRemoveDesignation(index)}>Remove</button>
            </div>
          ))}
        </fieldset>

        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default Compan;
