import React, { useState } from 'react';
import './CompanyForm.css';

const CompanyDetailsForm = () => {
  const [formData, setFormData] = useState({
    companyName: '',
    batch: '',
    address: {
      blockNo: '',
      buildingName: '',
      area: '',
      landmark: '',
      state: '',
      city: '',
      pincode: '',
    },
    contactPerson: {
      name: '',
      designation: '',
      email: '',
      mobile: '',
    },
    designations: [],
  });

  const handleChange = (field, value) => {
    setFormData({ ...formData, [field]: value });
  };

  const handleNestedChange = (section, field, value) => {
    setFormData({
      ...formData,
      [section]: {
        ...formData[section],
        [field]: value,
      },
    });
  };

  const handleDesignationChange = (index, field, value) => {
    const updatedDesignations = [...formData.designations];
    updatedDesignations[index][field] = value;
    setFormData({ ...formData, designations: updatedDesignations });
  };

  const handleProcessChange = (dIndex, pIndex, field, value) => {
    const updatedDesignations = [...formData.designations];
    updatedDesignations[dIndex].placementProcess[pIndex][field] = value;
    setFormData({ ...formData, designations: updatedDesignations });
  };

  const addDesignation = () => {
    setFormData({
      ...formData,
      designations: [
        ...formData.designations,
        {
          designation: '',
          package: '',
          bond: '',
          location: '',
          requiredQualifications: [],
          placementProcess: [],
        },
      ],
    });
  };

  const removeDesignation = (index) => {
    const updatedDesignations = formData.designations.filter(
      (_, idx) => idx !== index
    );
    setFormData({ ...formData, designations: updatedDesignations });
  };

  const addProcess = (dIndex) => {
    const updatedDesignations = [...formData.designations];
    updatedDesignations[dIndex].placementProcess.push({
      roundNumber: '',
      round: '',
      description: '',
    });
    setFormData({ ...formData, designations: updatedDesignations });
  };

  const removeProcess = (dIndex, pIndex) => {
    const updatedDesignations = [...formData.designations];
    updatedDesignations[dIndex].placementProcess = updatedDesignations[
      dIndex
    ].placementProcess.filter((_, idx) => idx !== pIndex);
    setFormData({ ...formData, designations: updatedDesignations });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form submitted:', formData);
    // Here, send the formData to your backend API
  };

  return (
    <form className="company-form" onSubmit={handleSubmit}>
      <h2>Company Details</h2>

      {/* Company Name */}
      <label>Company Name</label>
      <input
        type="text"
        value={formData.companyName}
        onChange={(e) => handleChange('companyName', e.target.value)}
        required
      />

      {/* Batch */}
      <label>Batch</label>
      <input
        type="text"
        value={formData.batch}
        onChange={(e) => handleChange('batch', e.target.value)}
        required
      />

      {/* Address */}
      <h3>Address</h3>
      <label>Block No</label>
      <input
        type="text"
        value={formData.address.blockNo}
        onChange={(e) => handleNestedChange('address', 'blockNo', e.target.value)}
        required
      />
      <label>Building Name</label>
      <input
        type="text"
        value={formData.address.buildingName}
        onChange={(e) => handleNestedChange('address', 'buildingName', e.target.value)}
        required
      />

      {/* Contact Person */}
      <h3>Contact Person</h3>
      <label>Name</label>
      <input
        type="text"
        value={formData.contactPerson.name}
        onChange={(e) => handleNestedChange('contactPerson', 'name', e.target.value)}
        required
      />
      <label>Designation</label>
      <input
        type="text"
        value={formData.contactPerson.designation}
        onChange={(e) => handleNestedChange('contactPerson', 'designation', e.target.value)}
        required
      />

      {/* Designations */}
      <h3>Designations</h3>
      {formData.designations.map((designation, dIndex) => (
        <div key={dIndex} className="designation">
          <label>Designation</label>
          <input
            type="text"
            value={designation.designation}
            onChange={(e) =>
              handleDesignationChange(dIndex, 'designation', e.target.value)
            }
            required
          />
          <label>Package</label>
          <input
            type="text"
            value={designation.package}
            onChange={(e) =>
              handleDesignationChange(dIndex, 'package', e.target.value)
            }
            required
          />
          <label>Bond</label>
          <input
            type="text"
            value={designation.bond}
            onChange={(e) =>
              handleDesignationChange(dIndex, 'bond', e.target.value)
            }
            required
          />
          <label>Location</label>
          <input
            type="text"
            value={designation.location}
            onChange={(e) =>
              handleDesignationChange(dIndex, 'location', e.target.value)
            }
            required
          />

          {/* Placement Process */}
          <h4>Placement Process</h4>
          {designation.placementProcess.map((process, pIndex) => (
            <div key={pIndex} className="placement-process">
              <label>Round Number</label>
              <input
                type="number"
                value={process.roundNumber}
                onChange={(e) =>
                  handleProcessChange(dIndex, pIndex, 'roundNumber', e.target.value)
                }
                required
              />
              <label>Round</label>
              <input
                type="text"
                value={process.round}
                onChange={(e) =>
                  handleProcessChange(dIndex, pIndex, 'round', e.target.value)
                }
                required
              />
              <label>Description</label>
              <textarea
                value={process.description}
                onChange={(e) =>
                  handleProcessChange(dIndex, pIndex, 'description', e.target.value)
                }
                required
              />
              <button
                type="button"
                onClick={() => removeProcess(dIndex, pIndex)}
              >
                Remove Round
              </button>
            </div>
          ))}
          <button type="button" onClick={() => addProcess(dIndex)}>
            Add Round
          </button>

          <button type="button" onClick={() => removeDesignation(dIndex)}>
            Remove Designation
          </button>
        </div>
      ))}
      <button type="button" onClick={addDesignation}>
        Add Designation
      </button>

      {/* Submit */}
      <button type="submit">Submit</button>
    </form>
  );
};

export default CompanyDetailsForm;
