import React, { useEffect, useState } from "react";
import StudentService from "../Services/StudentServices"; 
import ResultFilters from "./ResultFilters";
import "./CompanyDashboard.css";
import CompanyDeatilsForm from "./CompanyDetailsForm";


const CompanyDashboard = ({ selectedMenu }) => {
    const [Option, setOption] = useState("EnterDetails"); // Default to "EnterDetails" 
    const [filteredStudents, setFilteredStudents] = useState([]);
    const [students, setStudents] = useState([]);

    useEffect(() => {
        StudentService.getStudents()
          .then((response) => {
            setStudents(response.data);
            //setFilteredStudents(response.data);
          })
          .catch((error) => console.error("Error fetching students:", error));
      }, []);

    const handleOptionClick = (option) => {
        setOption(option); // Set the currently selected option
    }; 
    const handleFilter = (key, value) => {
        let filtered = [...students]; 
    
        if (key === "clear") {
          filtered=[];
          return;
        } 
    
        // Apply each filter condition progressively (AND logic)
        if (key === "batch" && value) {
            filtered = filtered.filter((s) => s.batch.toLowerCase() === s.batch.value.toLowerCase()); // Filter by batch
        }
    
        if (key === "program" && value) {
            filtered = filtered.filter((s) => s.program.toLowerCase() === s.program.value.toLowerCase()); // Filter by
        }
    
        if (key === "search" && value) {
          filtered = filtered.filter((s) =>s.name.toLowerCase().includes(value.toLowerCase()) // Filter by name
          );
        }
        setFilteredStudents(filtered);
        console.log(filtered);
      };
    
    const renderResultContent = () => {
        if (Option === "enterdetails") {
            return (
                <div className="enterdetails">
                    <CompanyDeatilsForm />
                </div>
            );
        }
        if (Option === "viewdetails") {
            return (
            <div className="viewdetails">
                 <ResultFilters onFilter={handleFilter} />
            </div>
            );
        }
        
        return null;
    };

    const renderContent = () => {
        if (selectedMenu === "Company") {
        return (
            <>                
            <div className="cmp-buttons">
            <div className="breadcrumb">
            Dashboard &gt; Company
            </div>
            <button onClick={() => handleOptionClick("enterdetails")}>
            <img src="/Images/Side errow.png" height="15px" width="15px" /> Enter Company Details
            </button>
            <br />
            <button onClick={() => handleOptionClick("viewdetails")}>
            <img src="/Images/Side errow.png" height="15px" width="15px" /> View Company Details
            </button>
            </div>

            {/* Render student-specific content */}
            <div className="main-section">{renderResultContent()}</div>
            </>
        );
        }
        else{
          <div></div>
        }

    };    
    
    return <div className="CompanyDashboard">{renderContent()}</div>;
};
export default CompanyDashboard;
