import React, { useState, useEffect } from "react";
// import "./CompanyFilter.css";

const CompanyFilters = ({ onFilter }) => {
  const [batch, setBatch] = useState("");
  const [companies, setCompanies] = useState([]); // Initialize companies as an array
  const [selectedCompany, setSelectedCompany] = useState("");
  const [program, setProgram] = useState("");
  const [search, setSearch] = useState("");

  useEffect(() => {
    // Fetch companies for the given batch
    const fetchCompanies = async () => {
      if (!batch) return; // Avoid fetching if no batch is selected

      try {
        const response = await fetch('http://localhost:8080/api/companies');
        if (response.ok) {
          const data = await response.json();
          setCompanies(data);
        } else {
          console.error("Failed to fetch companies");
        }
      } catch (error) {
        console.error("Error fetching companies:", error);
      }
    };

    fetchCompanies();
  }, [batch]); // Trigger fetch whenever batch changes

  // Function to handle filtering
  const handleFilterChange = (filterType, value) => {
    if (filterType === "batch") setBatch(value);
    if (filterType === "company") setSelectedCompany(value);
    if (filterType === "program") setProgram(value);
    if (filterType === "search") setSearch(value);

    // Notify parent about the filter change
    onFilter(filterType, value);
  };

  // Function to clear all filters
  const handleClearFilters = () => {
    const clearedFilters = {
      batch: "",
      company: "",
      program: "",
      search: "",
    };

    setBatch("");
    setSelectedCompany("");
    setProgram("");
    setSearch("");

    // Pass cleared filters as a single object
    onFilter("clear", clearedFilters);
  };

  return (
    <div className="CompanyFilter">
      {/* Batch Filter */}
      <div>
        <label>Batch</label>
        <select
          className="select"
          value={batch}
          onChange={(e) => handleFilterChange("batch", e.target.value)}
        >
          <option value="">Select</option>
          <option value="2018-2019">18-19</option>
          <option value="2019-2020">19-20</option>
          <option value="2020-2021">20-21</option>
          <option value="2021-2022">21-22</option>
          <option value="2022-2023">22-23</option>
          <option value="2023-2024">23-24</option>
        </select>
      </div>

      {/* Company Filter */}
      <div>
        <label>Company</label>
        <select
          className="select"
          value={selectedCompany}
          onChange={(e) => handleFilterChange("company", e.target.value)}
        >
          <option value="">Select</option>
          {companies.map((company) => (
            <option key={company.id} value={company.id}>
              {company.name}
            </option>
          ))}
        </select>
      </div>

      {/* Program Filter */}
      <div>
        <label>Program</label>
        <select
          className="select"
          value={program}
          onChange={(e) => handleFilterChange("program", e.target.value)}
        >
          <option value="">Select</option>
          <option value="MCA">MCA</option>
          <option value="B.Tech">B.Tech</option>
        </select>
      </div>

      {/* Search Filter */}
      <div className="search-container">
        <img
          src="/Images/Search.png"
          height="22px"
          width="22px"
          alt="search"
        />
        <input
          type="text"
          placeholder="Search..."
          value={search}
          onChange={(e) => handleFilterChange("search", e.target.value)}
        />
      </div>

      {/* Clear Filters Button */}
      <div>
        <button className="clear-btn" onClick={handleClearFilters}>
          Clear Filters
        </button>
      </div>
    </div>
  );
};

export default CompanyFilters;
