import React, { useEffect, useState } from "react";
import "./AnalysisDashboard.css";
import { Pie } from "react-chartjs-2";
import {
  Chart as ChartJS,
  ArcElement,
  Tooltip,
  Legend,
} from "chart.js";
import ChartDataLabels from "chartjs-plugin-datalabels"; // Import the plugin
import StudentService from "../Services/StudentServices";

ChartJS.register(ArcElement, Tooltip, Legend, ChartDataLabels); // Register the plugin

const AnalysisDashboard = () => {
  const [placedCount, setPlacedCount] = useState(0);
  const [nonPlacedCount, setNonPlacedCount] = useState(0);
  const [pending, setPendingCount] = useState(0);
  const [loading, setLoading] = useState(true);
  
  const handleFilterChange = (filterType, value) => {
    if (filterType === "batch") setBatch(value);
    if (filterType === "program") setProgram(value);
    if (filterType === "search") setSearch(value);
   
    onFilter(filterType, value); // Notify parent about the filter change
  };

  // Function to clear all filters
  const handleClearFilters = () => {
    const clearedFilters = {
      batch: "",
      program: "",
      search: "",
    };

    setBatch("");
    setProgram("");
    setSearch("");
    
    // Pass cleared filters as a single object
    onFilter("clear", clearedFilters);
  };
  useEffect(() => {
    StudentService.getStudents()
      .then((response) => {
        const students = response.data;
        console.log("Students Data:", students);
        const placed = students.filter((student) => student.placement_status === "placed").length;
        const nonPlaced = students.filter((student) => student.placement_status === "not placed").length;
        const pending = students.filter((student) => student.placement_status === "pending").length;

        setPlacedCount(placed);
        setNonPlacedCount(nonPlaced);
        setPendingCount(pending);
      })
      .catch((error) => console.error("Error fetching student data:", error))
      .finally(() => setLoading(false));
  }, []);

  const pieChartData = {
    labels: ["Placed Students", "Non-Placed Students", "Pending"],
    datasets: [
      {
        data: [placedCount, nonPlacedCount, pending],
        backgroundColor: ["#4CAF50", "#FF5252", "purple"],
        hoverBackgroundColor: ["#66BB6A", "#FF867F", "#A020F0"],
        borderColor: ["#ffffff", "#ffffff", "#ffffff"],
        borderWidth: 2,
      },
    ],
  };

  const pieChartOptions = {
    responsive: true,
    maintainAspectRatio: true,
    plugins: {
      legend: { position: "top" },
      tooltip: { enabled: true },
      datalabels: {
        color: "#ffffff", // Text color
        font: {
          size: 14,
          weight: "bold",
        },
        formatter: (value, context) => {
          return `${value}`; // Display the value
        },
      },
    },
  };

  if (loading) {
    return <div className="loading">Loading data...</div>;
  }

  return (
    <div className="analysis-dashboard">
      <Pie data={pieChartData} options={pieChartOptions} />
    </div>
  );
};

export default AnalysisDashboard;
