import React, { useEffect, useState } from "react";
import { Bar } from "react-chartjs-2"; // Import Bar chart from Chart.js
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";
import StudentService from "../Services/StudentServices"; // Import your service to fetch student data

// Register Chart.js components
ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

const AnalysisDashboard = () => {
  const [placedCount, setPlacedCount] = useState(0);
  const [nonPlacedCount, setNonPlacedCount] = useState(0);

  // Fetch student data and calculate placed/non-placed counts
  useEffect(() => {
    StudentService.getStudents()
      .then((response) => {
        const students = response.data;
        const placed = students.filter((student) => student.placementStatus === "placed").length;
        const nonPlaced = students.filter((student) => student.placementStatus !== "not placed").length;
        const pending = students.filter((student) => student.placementStatus !== "not placed").length;
       
        setPlacedCount(placed);
        setNonPlacedCount(nonPlaced);
      })
      .catch((error) => console.error("Error fetching student data:", error));
  }, []);

  // Data for the chart
  const chartData = {
    labels: ["Placed Students", "Non-Placed Students","pending"],
    datasets: [
      {
        label: "Placement Status",
        data: [placedCount, nonPlacedCount],
        backgroundColor: ["rgba(75, 192, 192, 0.6)", "rgba(255, 99, 132, 0.6)"],
        borderColor: ["rgba(75, 192, 192, 1)", "rgba(255, 99, 132, 1)"],
        borderWidth: 1,
      },
    ],
  };

  // Chart options
  const chartOptions = {
    responsive: true,
    plugins: {
      legend: { position: "top" },
      title: { display: true, text: "Placed vs Non-Placed Students" },
    },
  };

  return (
    <div className="analysis-dashboard">
      <h2>Placement Analysis</h2>
      <Bar data={chartData} options={chartOptions} />
    </div>
  );
};

export default AnalysisDashboard;
