import React, { useState } from "react";
import "./Sidebar.css";

function Sidebar({ onMenuSelect }) {
  const [isStudentMenuOpen, setStudentMenuOpen] = useState(false);

  const handleStudentClick = () => {
    setStudentMenuOpen(!isStudentMenuOpen);
  };

  return (
    <div className="sidebar">
      <div className="menu-item" onClick={() => onMenuSelect("AdminProfile")}>
        <img src="/Images/Threeline.png" alt="Dashboard Icon" className="menu-icon" />
        <span className="menu-text">Admin-Profile</span>
      </div>
      <div className="menu-item" onClick={handleStudentClick}>
        <img src="/Images/Threeline.png" alt="Dashboard Icon" className="menu-icon" />
        <span className="menu-text">Student</span>
      </div>
      {isStudentMenuOpen && (
        <div className="submenu">
          <div className="submenu-item" onClick={() => onMenuSelect("EnterDetails")}>
            Enter Details
          </div>
          <div className="submenu-item" onClick={() => onMenuSelect("ViewDetails")}>
            View Details
          </div>
          <div className="submenu-item" onClick={() => onMenuSelect("DownloadReports")}>
            Download Reports
          </div>
        </div>
      )}
      <div className="menu-item" onClick={() => onMenuSelect("Company")}>
        <img src="/Images/Threeline.png" alt="Dashboard Icon" className="menu-icon" />
        <span className="menu-text">Company</span>
      </div>
      <div className="menu-item" onClick={() => onMenuSelect("Analysis")}>
        <img src="/Images/Threeline.png" alt="Dashboard Icon" className="menu-icon" />
        <span className="menu-text">Analysis</span>
      </div>
    </div>
  );
}

export default Sidebar;
