import React, { useState } from "react";
import "./loginpage.css";

function LoginPage({ onLogin, navigateToSignup, navigateToDashboard }) {
  const [formData, setFormData] = useState({ email: "", password: "" });
  const [errorMessage, setErrorMessage] = useState("");

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setErrorMessage(""); // Reset error message
    try {
      const response = await fetch("http://localhost:8080/api/auth/login", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formData),
      });

      if (response.ok) {
        alert("Login successful! Redirecting to dashboard...");
        navigateToDashboard(); // Redirect to the dashboard
      } else {
        const error = await response.text();
        setErrorMessage(error);
      }
    } catch (error) {
      console.error("Error during login:", error);
      setErrorMessage("Something went wrong! Please try again.");
    }
  };

  return (
    <div className="LoginPage_Container">
      <div className="Login_image">
        <h3>Placement Assistant</h3>
        {/* Optional: Add an image */}
        {/* <img src="/Images/login_Illustration.png" alt="Login Illustration" /> */}
      </div>
      <div className="LoginPage">
        <h3>Login</h3>
        {errorMessage && <p className="error-message">{errorMessage}</p>} {/* Display error messages */}
        <form onSubmit={handleSubmit}>
          {/* Email Field */}
          <div className="email">
            <label htmlFor="email" className="form-label">Email Address</label>
            <input
              type="email"
              className="form-control"
              id="email"
              name="email"
              placeholder="Enter your email"
              value={formData.email}
              onChange={handleChange}
              required
            />
          </div>

          {/* Password Field */}
          <div className="pwd">
            <label htmlFor="password" className="form-label">Password</label>
            <input
              type="password"
              className="form-control"
              id="password"
              name="password"
              placeholder="Enter your password"
              value={formData.password}
              onChange={handleChange}
              required
            />
          </div>

          {/* Buttons */}
          <div className="LSbuttons">
            <button type="submit" className="login" onclick={navigateto}>
              Login
            </button>
            <button
              type="button"
              className="signup"
              onClick={navigateToSignup} // Navigate to signup page
            >
              Sign up
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default LoginPage;
