import React, { useState } from "react";
//import "bootstrap/dist/css/bootstrap.min.css";
import LoginHeader from "./LoginHeader";
import "./loginpage.css"; // Optional for additional styles

function LoginPage({onLogin}) {
  const [formData, setFormData] = useState({ name:"" ,eno:"",email: "", password: "" });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };
 
  const handleSubmit = (e) => {
    e.preventDefault();
    // You can replace this alert with actual login logic
    alert(`Login Successful!\nEmail: ${formData.email}\nPassword: ${formData.password}`);
  };

  return (
    <div> <LoginHeader />
    <div className="LoginPage_Container">
     
      <div className="Login_image">
      <img src="/Images/login_Illustration.png"  />
      </div>
      <div className="LoginPage">
        <h3>Login</h3>
        <form onSubmit={handleSubmit}>
        
         
          {/* Email Field */}
          <div className="email">
            <label htmlFor="email" className="form-label">Email Address</label>
            <input
              type="email"
              className="form-control"
              id="email"
              name="email"
              placeholder="Enter your email"
              value={formData.email}
              onChange={handleChange}
              required
            />
          </div>
         

          {/* Password Field */}
          <div className="pwd">
            <label htmlFor="password" className="form-label">Password</label>
            <input
              type="password"
              className="form-control"
              id="password"
              name="password"
              placeholder="Enter your password"
              value={formData.password}
              onChange={handleChange}
              required
            />
          </div>

          {/* Login Button */}
          <button type="submit" className="btn btn-primary w-100">
            Login
          </button>
        </form>
      </div>
    </div>
    </div>
  );
}

export default LoginPage;
