import Sidebar from "./Components/Sidebar";
import Header from "./Components/Header";
import Dashboard from "./Components/Dashboard";
import AnalysisDashboard from "./Components/AnalysisDashboard"; // Import the new AnalysisDashboard component
import React, { useState } from "react";
import "./App.css"; // Ensure you include global styles
import StudentDetails from "./Components/StudentDetails";
import StudentDetailsDashboard from "./Components/StudentDetailDashboard";
import LoginPage from "./Components/loginpage";

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false); // Track login status
  const [selectedMenu, setSelectedMenu] = useState(); 


  const handleLogin = () => {
    setIsLoggedIn(true); // Set login state to true on successful login
  };
  const renderContent = () => {
    switch (selectedMenu) {
      case "StudentProfile":
        return <LoginPage />;
      case "StudentDetail":
        return <StudentDetailsDashboard />;
      case "Company":
        return <div>Company Content</div>;
      case "Analysis":
        return <AnalysisDashboard />;
      default:
        return <div>Select a menu item to get started.</div>;
    }
  };

  return (
    <div className="app">
      {!isLoggedIn ? (
        <LoginPage onLogin={handleLogin} /> // Pass the login handler
      ) : (
        <>
      <Sidebar onMenuSelect={setSelectedMenu} /> {/* Pass the menu selection handler */}
      <div className="main-content">
        <Header />
        <div className="content">{renderContent()}</div> {/* Display the correct content */}

    </div>
    </>
    )}
    </div>
  );
}

export default App;
